import{_ as t,c as a,o as r,ag as i}from"./chunks/framework.DSsEZrhx.js";const u=JSON.parse('{"title":"Аналіз предметної області","description":"","frontmatter":{"title":"Аналіз предметної області","outline":"deep"},"headers":[],"relativePath":"requirements/state-of-the-art.md","filePath":"requirements/state-of-the-art.md"}'),l={name:"requirements/state-of-the-art.md"};function o(n,e,h,s,d,D){return r(),a("div",null,e[0]||(e[0]=[i('<h1 id="аналіз-предметноі-області" tabindex="-1">Аналіз предметної області <a class="header-anchor" href="#аналіз-предметноі-області" aria-label="Permalink to &quot;Аналіз предметної області&quot;">​</a></h1><h2 id="вступ" tabindex="-1">Вступ <a class="header-anchor" href="#вступ" aria-label="Permalink to &quot;Вступ&quot;">​</a></h2><p>У цьому розділу пропонується розглянути предметну область системи організації та управління опитуваннями експертів. Метою цього розділу є аналіз існуючих підходів до вирішення завдань, пов&#39;язаних з опитуваннями експертів, а також порівняння наявних аналогів системи за властивостями FURPS (функціональність, надійність, використання, продуктивність та підтримка). У результаті цього аналізу очікується отримання рекомендацій для створення нової системи, яка зможе задовольнити потреби різних категорій користувачів та забезпечити ефективне управління опитуваннями експертів.</p><h2 id="основні-визначення" tabindex="-1">Основні визначення <a class="header-anchor" href="#основні-визначення" aria-label="Permalink to &quot;Основні визначення&quot;">​</a></h2><h3 id="експерт" tabindex="-1">Експерт <a class="header-anchor" href="#експерт" aria-label="Permalink to &quot;Експерт&quot;">​</a></h3><p><strong>Експерт</strong> - висококваліфікований спеціаліст, який має вищу освіту, відповідну кваліфікацію і професійні знання з питань, що досліджуються, виконує службові обов&#39;язки, пов&#39;язані з провадженням діяльності у відповідній галузі, безпосередньо проводить експертизу та несе персональну відповідальність за достовірність і повноту аналізу, обґрунтованість висновків відповідно до завдання на проведення експертизи. <a href="https://uk.wikipedia.org/wiki/%D0%95%D0%BA%D1%81%D0%BF%D0%B5%D1%80%D1%82" target="_blank" rel="noreferrer">[12]</a></p><h3 id="ітерація" tabindex="-1">Ітера́ція <a class="header-anchor" href="#ітерація" aria-label="Permalink to &quot;Ітера́ція&quot;">​</a></h3><p><strong>Ітера́ція</strong> (від лат. iteratio — повторювання) — багатозначний термін, який залежно від контексту може означати:</p><ul><li>Повторне застосування математичної операції (із зміненими даними) при розв&#39;язанні обчислювальних задач, яке дає можливість поступово наблизитися до правильного результату.</li><li>Результат багаторазового повторення якоїсь математичної операції. <a href="https://uk.wikipedia.org/wiki/%D0%86%D1%82%D0%B5%D1%80%D0%B0%D1%86%D1%96%D1%8F" target="_blank" rel="noreferrer">[13]</a></li></ul><h3 id="контраргумент" tabindex="-1">Контраргумент <a class="header-anchor" href="#контраргумент" aria-label="Permalink to &quot;Контраргумент&quot;">​</a></h3><p><strong>Контраргумент</strong> - підстава, доказ, які наводять для обґрунтування, підтвердження думки, що суперечить поглядам співрозмовника, чиїмсь аргументам. <a href="https://goroh.pp.ua/%D0%A2%D0%BB%D1%83%D0%BC%D0%B0%D1%87%D0%B5%D0%BD%D0%BD%D1%8F/%D0%BA%D0%BE%D0%BD%D1%82%D1%80%D0%B0%D1%80%D0%B3%D1%83%D0%BC%D0%B5%D0%BD%D1%82" target="_blank" rel="noreferrer">[14]</a></p><h3 id="алгоритм" tabindex="-1">Алгори́тм <a class="header-anchor" href="#алгоритм" aria-label="Permalink to &quot;Алгори́тм&quot;">​</a></h3><p><strong>Алгори́тм</strong> (латинізов. Algorithmi за араб. ім&#39;ям перського математика аль-Хорезмі) — набір інструкцій, які описують порядок дій виконавця, щоб досягти результату розв&#39;язання задачі за скінченну кількість дій. Для візуалізації алгоритмів часто використовують блок-схеми. <a href="https://uk.wikipedia.org/wiki/%D0%90%D0%BB%D0%B3%D0%BE%D1%80%D0%B8%D1%82%D0%BC" target="_blank" rel="noreferrer">[15]</a></p><h3 id="google-forms" tabindex="-1">Google Forms <a class="header-anchor" href="#google-forms" aria-label="Permalink to &quot;Google Forms&quot;">​</a></h3><p><strong>Google Forms</strong> (англ. Google Forms) — програмне забезпечення для адміністрування опитування, що входить до складу безкоштовного вебпакету Google Docs Editors, пропонованого Google. Додаток дозволяє користувачам створювати та редагувати опитування в Інтернеті, співпрацюючи з іншими користувачами в режимі реального часу. Зібрану інформацію можна автоматично внести в електронну таблицю. <a href="https://workspace.google.com/products/forms/" target="_blank" rel="noreferrer">[4]</a></p><h3 id="surveymonkey" tabindex="-1">SurveyMonkey <a class="header-anchor" href="#surveymonkey" aria-label="Permalink to &quot;SurveyMonkey&quot;">​</a></h3><p><strong>SurveyMonkey</strong> - це сервіс для створення опитувань, а також масової розсилки анкет і виявлення тенденцій. Сервіс дозволяє швидко створювати опитування, налаштовувати їх вигляд, проводити A/B тестування, інтегрувати опитування на сайтах та соціальних мережах, створювати дуже детальні та візуальні звіти, захищати дані. <a href="https://www.surveymonkey.com/product/" target="_blank" rel="noreferrer">[1]</a></p><h3 id="survio" tabindex="-1">Survio <a class="header-anchor" href="#survio" aria-label="Permalink to &quot;Survio&quot;">​</a></h3><p><strong>Survio</strong> - просте у використанні програмне забезпечення для опитувань задоволеності клієнтів, зворотного зв&#39;язку від співробітників, дослідження ринку та інших онлайн-опитувань. <a href="https://www.survio.com/en/features" target="_blank" rel="noreferrer">[2]</a></p><h3 id="surveyjs" tabindex="-1">SurveyJS <a class="header-anchor" href="#surveyjs" aria-label="Permalink to &quot;SurveyJS&quot;">​</a></h3><p><strong>SurveyJS</strong> — це набір компонентів JavaScript, які дозволяють вам і вашим користувачам створювати опитування, вікторини, опитування та інші веб-форми, зберігати їх у вашій базі даних і візуалізувати результати опитування на користувацьких інформаційних панелях. <a href="https://surveyjs.io/documentation" target="_blank" rel="noreferrer">[3]</a></p><h3 id="oca" tabindex="-1">&quot;OCA&quot; <a class="header-anchor" href="#oca" aria-label="Permalink to &quot;&quot;OCA&quot;&quot;">​</a></h3><p><strong>&quot;OCA&quot;</strong> - (абревіатура від Обробка Соціологічних Анкет) призначений для організації введення та статистичного аналізу результатів соціологічних опитувань. <a href="https://oca.com.ua/index.php?t=101" target="_blank" rel="noreferrer">[16]</a></p><h3 id="google-sheets" tabindex="-1">Google Sheets <a class="header-anchor" href="#google-sheets" aria-label="Permalink to &quot;Google Sheets&quot;">​</a></h3><p><strong>Google Sheets</strong> - додаток для роботи з електронними таблицями, що входить до складу безкоштовного вебпрограмного пакету програмного забезпечення, пропонованого компанією Google у межах служби Google Диск. <a href="https://uk.wikipedia.org/wiki/Google_%D0%A2%D0%B0%D0%B1%D0%BB%D0%B8%D1%86%D1%96" target="_blank" rel="noreferrer">[17]</a></p><h3 id="хмарні-сервіси" tabindex="-1">Хмарні сервіси <a class="header-anchor" href="#хмарні-сервіси" aria-label="Permalink to &quot;Хмарні сервіси&quot;">​</a></h3><p><strong>Хмарні сервіси</strong> (public cloud services) ― це програми та платформи, які «живуть» та працюють на серверах хмарних операторів. Їхня головна особливість полягає в тому, що створюючи акаунт на такій платформі, людина зможе отримувати доступ до власної інформації з будь-якого гаджета в будь-якій точці світу. <a href="https://gigacloud.ua/articles/shho-take-hmarni-servisy-ta-yak-vony-dopomagayut-biznesu/" target="_blank" rel="noreferrer">[18]</a></p><h3 id="розширена-аналітика" tabindex="-1">Розширена аналітика <a class="header-anchor" href="#розширена-аналітика" aria-label="Permalink to &quot;Розширена аналітика&quot;">​</a></h3><p><strong>Розширена аналітика</strong> – це автоматизований процес отримання корисної інформації (висновків) з великого обсягу даних. <a href="https://www.facebook.com/story.php?story_fbid=725172066079259&amp;id=100057594153145&amp;_rdr" target="_blank" rel="noreferrer">[19]</a></p><h3 id="інтерфеис" tabindex="-1">Інтерфейс <a class="header-anchor" href="#інтерфеис" aria-label="Permalink to &quot;Інтерфейс&quot;">​</a></h3><p><strong>Інтерфейс</strong> (від англ. interface — поверхня розділу, перегородка) — сукупність засобів, методів і правил взаємодії (керування, контролю, тощо) між елементами системи. <a href="https://uk.wikipedia.org/wiki/%D0%86%D0%BD%D1%82%D0%B5%D1%80%D1%84%D0%B5%D0%B9%D1%81" target="_blank" rel="noreferrer">[20]</a></p><h3 id="api" tabindex="-1">API <a class="header-anchor" href="#api" aria-label="Permalink to &quot;API&quot;">​</a></h3><p><strong>API</strong> — це посередник між програмами, який задає правила «спілкування». Тому в його назві закладено поняття «інтерфейс» — межа між об’єктами. Одній програмі не важливо, як працює інша. Розробники просто користуються інтерфейсом: надсилають запит і отримують зворотну відповідь. За типом доступу API поділяють на:</p><ul><li>Публічні API. Розробники створили API для своєї програми та дають користуватися ним усьому світу. Такі API часто продають, щоб отримати додатковий дохід. Наприклад, так робить Google з API Google Maps.</li><li>Приватні чи внутрішні API. Розробники приховують ці API від всього світу. Наприклад, приватні API використовують компанії, щоб зв’язати свої програми.</li><li>Партнерські API. Спеціальні API, які розробники компаній пишуть для партнерів та діляться лише з ними. Наприклад, API для зв’язку бази даних компанії та сторонньої CRM-системи чи email-сервісу.</li><li>Складні API. Складаються з кількох API, щоб виконувати багато послідовних дій. Зазвичай складні API створюють, тому що вони працюють швидше, ніж один великий API. <a href="https://hostiq.ua/blog/ukr/what-is-api/" target="_blank" rel="noreferrer">[21]</a></li></ul><h3 id="кастомізація" tabindex="-1">Кастомізація <a class="header-anchor" href="#кастомізація" aria-label="Permalink to &quot;Кастомізація&quot;">​</a></h3><p><strong>Кастомізація</strong> (від англ. customize — налаштовувати) — процес адаптації та налаштування продукту під окрему аудиторію, об&#39;єднану певними особливостями. Як правило, термін кастомізація застосовується у сфері сучасної техніки як завершальний етап перед випуском продукції. <a href="https://uk.wikipedia.org/wiki/%D0%9A%D0%B0%D1%81%D1%82%D0%BE%D0%BC%D1%96%D0%B7%D0%B0%D1%86%D1%96%D1%8F" target="_blank" rel="noreferrer">[22]</a></p><h2 id="підходи-та-способи-вирішення-завдання" tabindex="-1">Підходи та способи вирішення завдання <a class="header-anchor" href="#підходи-та-способи-вирішення-завдання" aria-label="Permalink to &quot;Підходи та способи вирішення завдання&quot;">​</a></h2><h3 id="_1-метод-незалежних-характеристик" tabindex="-1">1. Метод незалежних характеристик <a class="header-anchor" href="#_1-метод-незалежних-характеристик" aria-label="Permalink to &quot;1. Метод незалежних характеристик&quot;">​</a></h3><p>Цей метод дозволяє отримати узагальнену оцінку конкретної проблеми або ситуації на основі інформації, зібраної від різних незалежних джерел. Він складається з таких етапів:</p><ul><li>Аналіз та порівняння різних точок зору;</li><li>Обробка отриманих даних за допомогою математичних і статистичних методів;</li><li>Формулювання обґрунтованих висновків.</li></ul><h3 id="_2-метод-дельфі" tabindex="-1">2. Метод &quot;Дельфі&quot; <a class="header-anchor" href="#_2-метод-дельфі" aria-label="Permalink to &quot;2. Метод &quot;Дельфі&quot;&quot;">​</a></h3><p>Передбачає анонімне висловлення думок експертами щодо заданої проблеми. Після кожного етапу учасники отримують узагальнений звіт, що дозволяє їм коригувати свої оцінки. Процес повторюється доти, доки думки не стабілізуються. <a href="https://uk.wikipedia.org/wiki/%D0%9C%D0%B5%D1%82%D0%BE%D0%B4_%D0%94%D0%B5%D0%BB%D1%8C%D1%84%D1%96" target="_blank" rel="noreferrer">[6]</a></p><h3 id="_3-метод-мозкового-штурму" tabindex="-1">3. Метод мозкового штурму <a class="header-anchor" href="#_3-метод-мозкового-штурму" aria-label="Permalink to &quot;3. Метод мозкового штурму&quot;">​</a></h3><p>Група експертів генерує ідеї без будь-яких обмежень або критики. Це сприяє вільному обговоренню та пошуку нестандартних рішень. <a href="https://uk.wikipedia.org/wiki/%D0%9C%D0%BE%D0%B7%D0%BA%D0%BE%D0%B2%D0%B8%D0%B9_%D1%88%D1%82%D1%83%D1%80%D0%BC" target="_blank" rel="noreferrer">[7]</a></p><h3 id="_4-метод-зворотного-мозкового-штурму" tabindex="-1">4. Метод зворотного мозкового штурму <a class="header-anchor" href="#_4-метод-зворотного-мозкового-штурму" aria-label="Permalink to &quot;4. Метод зворотного мозкового штурму&quot;">​</a></h3><p>Подібний до звичайного мозкового штурму, але акцент робиться на пошук недоліків і критичних аспектів запропонованих ідей. <a href="https://en.wikipedia.org/wiki/Scenario_analysis" target="_blank" rel="noreferrer">[8]</a></p><h3 id="_5-метод-635" tabindex="-1">5. Метод &quot;635&quot; <a class="header-anchor" href="#_5-метод-635" aria-label="Permalink to &quot;5. Метод &quot;635&quot;&quot;">​</a></h3><p>Цей метод передбачає участь шести експертів, кожен з яких за п’ять хвилин генерує три ідеї. Процес триває протягом пів години, у результаті чого загалом формується 108 ідей. <a href="https://en.wikipedia.org/wiki/Brainwriting" target="_blank" rel="noreferrer">[9]</a></p><h3 id="_6-метод-прогнозних-сценаріів" tabindex="-1">6. Метод прогнозних сценаріїв <a class="header-anchor" href="#_6-метод-прогнозних-сценаріів" aria-label="Permalink to &quot;6. Метод прогнозних сценаріїв&quot;">​</a></h3><p>Передбачає поетапне формування прогнозів шляхом узагальнення експертних думок, що проходить кілька ітерацій до моменту досягнення спільної оцінки.</p><h3 id="_7-метод-поминальних-груп" tabindex="-1">7. Метод поминальних груп <a class="header-anchor" href="#_7-метод-поминальних-груп" aria-label="Permalink to &quot;7. Метод поминальних груп&quot;">​</a></h3><p>Починається з індивідуальних інтерв’ю експертів, результати яких потім обговорюються іншими фахівцями для узгодження чи спростування отриманих оцінок.</p><h3 id="_8-метод-написання-сценарію" tabindex="-1">8. Метод написання сценарію <a class="header-anchor" href="#_8-метод-написання-сценарію" aria-label="Permalink to &quot;8. Метод написання сценарію&quot;">​</a></h3><p>Описує ймовірний розвиток подій на основі аналізу поточної ситуації та прогнозованих змін. Включає структурний аналіз, ідентифікацію ключових факторів та розробку практичних рекомендацій.</p><h3 id="_9-метод-колективного-блокнота" tabindex="-1">9. Метод колективного блокнота <a class="header-anchor" href="#_9-метод-колективного-блокнота" aria-label="Permalink to &quot;9. Метод колективного блокнота&quot;">​</a></h3><p>Поєднує незалежне формулювання ідей експертами з подальшим колективним аналізом і обговоренням.</p><h3 id="_10-метод-kj-діаграма-спорідненості" tabindex="-1">10. Метод KJ (діаграма спорідненості) <a class="header-anchor" href="#_10-метод-kj-діаграма-спорідненості" aria-label="Permalink to &quot;10. Метод KJ (діаграма спорідненості)&quot;">​</a></h3><p>Використовується для впорядкування великого обсягу інформації та ідей, систематизації поглядів і визначення зв’язків між ними. <a href="https://en.wikipedia.org/wiki/Affinity_diagram" target="_blank" rel="noreferrer">[10]</a></p><h3 id="_11-експертне-фокусування" tabindex="-1">11. Експертне фокусування <a class="header-anchor" href="#_11-експертне-фокусування" aria-label="Permalink to &quot;11. Експертне фокусування&quot;">​</a></h3><p>Передбачає безпосереднє обговорення проблеми експертами з метою її глибокого аналізу та виявлення ключових взаємозв’язків.</p><h3 id="_12-метод-неспеціаліста" tabindex="-1">12. Метод неспеціаліста <a class="header-anchor" href="#_12-метод-неспеціаліста" aria-label="Permalink to &quot;12. Метод неспеціаліста&quot;">​</a></h3><p>Рішення шукають особи, які не є фахівцями у вузькій темі, проте мають досвід у суміжних галузях, що сприяє нестандартному підходу до вирішення проблеми.</p><h3 id="_13-метод-комісіі" tabindex="-1">13. Метод комісії <a class="header-anchor" href="#_13-метод-комісіі" aria-label="Permalink to &quot;13. Метод комісії&quot;">​</a></h3><p>Передбачає відкриту дискусію експертів з метою формування єдиної думки щодо проблеми. Учасники можуть ставити запитання, обговорювати аргументи та змінювати свою позицію залежно від отриманої інформації.</p><h3 id="_14-метод-суду" tabindex="-1">14. Метод суду <a class="header-anchor" href="#_14-метод-суду" aria-label="Permalink to &quot;14. Метод суду&quot;">​</a></h3><p>Створюються дві команди експертів: одна захищає певну позицію, інша – висловлює контраргументи. Третя група (&quot;судді&quot;) приймає остаточне рішення після обговорення.</p><h3 id="_15-метод-експертних-оцінок" tabindex="-1">15. Метод експертних оцінок <a class="header-anchor" href="#_15-метод-експертних-оцінок" aria-label="Permalink to &quot;15. Метод експертних оцінок&quot;">​</a></h3><p>Залучає групу кваліфікованих експертів для оцінки складних проблем, що не піддаються формалізації. Включає анкетування та аналіз отриманих відповідей. <a href="https://www.sciencedirect.com/topics/computer-science/expert-judgment" target="_blank" rel="noreferrer">[11]</a></p><h3 id="_16-метод-евристичного-прогнозування" tabindex="-1">16. Метод евристичного прогнозування <a class="header-anchor" href="#_16-метод-евристичного-прогнозування" aria-label="Permalink to &quot;16. Метод евристичного прогнозування&quot;">​</a></h3><p>Передбачає детальне обґрунтування прогнозів, перевірку компетентності експертів і застосування спеціальних алгоритмів для обробки зібраної інформації.</p><p>Ці методи допомагають отримати обґрунтовані оцінки, розробити ефективні рішення та передбачити можливі сценарії розвитку подій.</p><h2 id="порівняльна-характеристика-існуючих-засобів-вирішення-завдання" tabindex="-1">Порівняльна характеристика існуючих засобів вирішення завдання <a class="header-anchor" href="#порівняльна-характеристика-існуючих-засобів-вирішення-завдання" aria-label="Permalink to &quot;Порівняльна характеристика існуючих засобів вирішення завдання&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;"></th><th style="text-align:center;">Functionality</th><th style="text-align:center;">Usability</th><th style="text-align:center;">Reliability</th><th style="text-align:center;">Performance</th><th style="text-align:center;">Supportability</th></tr></thead><tbody><tr><td style="text-align:left;">Google Forms</td><td style="text-align:center;">Надає широкий спектр можливостей для створення онлайн-форм, таблиць та ін. Добре інтегрований з іншими сервісами та має можливість збирати відповіді з Google Sheets.</td><td style="text-align:center;">Google Forms відзначається високим рівнем зручності. Простий інтуїтивний інтерфейс дозволяє працювати з сервісом навіть початківцям.</td><td style="text-align:center;">Інфраструктура Google дуже розвинена, стабільна робота хмарних сервісів.</td><td style="text-align:center;">Дуже швидко працює в будь яких умовах.</td><td style="text-align:center;">Google Forms має широку базу підтримки через офіційну документацію та підтримку. Крім того, є величезна кількість навчальних матеріалів для роботи.</td></tr><tr><td style="text-align:left;">SurveyMonkey</td><td style="text-align:center;">Розширені функції аналітики, можливість створення складних опитувань, широкий вибір шаблонів.</td><td style="text-align:center;">Достатньо зрозумілий інтерфейс але велика кількість функцій може ускладнити освоєння.</td><td style="text-align:center;">Стабільна та якісна підтримка.</td><td style="text-align:center;">Розширена аналітика? інтеграції з різними сервісами. Висока продуктивність для великих проєктів.</td><td style="text-align:center;">Наявна велкика команда підтримки для користувачів з різними засобами зв&#39;язку, є доступні навчальні матеріали.</td></tr><tr><td style="text-align:left;">Survio</td><td style="text-align:center;">Зрозумілий інтерфейс і великий вибір шаблонів. Безкоштовна версія має обмеження.</td><td style="text-align:center;">Інтерфейс дружній до користувача. Менше аналітичних можливостей, але зручно для базових опитуваннь.</td><td style="text-align:center;">Для складних завданнь обмежена функціональність. Підходить для невеликих проектів.</td><td style="text-align:center;">Дуже швидкі оптимізовані засоби для роботи, простий API для інтеграцій.</td><td style="text-align:center;">Наявність онлайн-форм для запитів та швидкої підтримки, зручні інструкції.</td></tr><tr><td style="text-align:left;">SurveyJS</td><td style="text-align:center;">Широкі можливості для кастомізації, інтеграція з веб-додатками.</td><td style="text-align:center;">Потребує часу для освоєння, підходить для фахівців.</td><td style="text-align:center;">Надійність залежить від кваліфікації користувача.</td><td style="text-align:center;">Продуктивність залежить від корситувацьких налаштувань. Є обмеження у роботі з великою кількістю даних.</td><td style="text-align:center;">Немає централізованої служби підтримки, але є підтримка через форуми та спільноти розробників.</td></tr><tr><td style="text-align:left;">&quot;OCA&quot;</td><td style="text-align:center;">Можливість адаптації під специфічні потреби проекту. Вимагає більше часу і зусилль.</td><td style="text-align:center;">Складний інтерфейс, можлива кастомізація. Наявні засоби компалексного форматування.</td><td style="text-align:center;">Наявні всі базові можливості такі як оновлення та підтримка, захист даних.</td><td style="text-align:center;">Централізоване управління даними з можливістю автоматизації, гнучкі налаштування. Можуть виникати затримки через складність системи, високі вимоги до серверних ресурсів.</td><td style="text-align:center;">Можливість отримання цілеспрямованої підтримки для конкретного проекту, але може вимагати багато уваги для забезпечення.</td></tr><tr><td style="text-align:left;">Voice of Experts</td><td style="text-align:center;">Доступні всі базові функції для створення опитуваннь та роботи з ними. Управління списками респондентів та автоматизований збір відповідей. Можлива генерація аналітичних звітів.</td><td style="text-align:center;">Інтуїтивно зрозумілий інтерфейс, різна локалізація. Адаптовано для мобільних пристроїв.</td><td style="text-align:center;">Відновлення системи у випадку пошкодження даних, надійний захист.</td><td style="text-align:center;">Оптимізовано для швидкої обробки та роботи з великими обсягами даних.</td><td style="text-align:center;">Наявна документація для користувачів, гнучкі налаштування системи.</td></tr></tbody></table><h2 id="висновки" tabindex="-1">Висновки <a class="header-anchor" href="#висновки" aria-label="Permalink to &quot;Висновки&quot;">​</a></h2><p>Система організації та управління опитуваннями експертів є ключовим інструментом для збору та аналізу думок спеціалістів у різних галузях. Серед розглянутих рішень найбільш функціональним варіантом є &quot;SurveyMonkey&quot;, проте ця платформа має певні недоліки, зокрема складний інтерфейс і відсутність безкоштовного доступу для масових опитувань. Це створює передумови для розробки альтернативної системи, яка надаватиме користувачам більш зручний і доступний механізм збору експертних відповідей без фінансових обмежень.</p><h2 id="посилання" tabindex="-1">Посилання <a class="header-anchor" href="#посилання" aria-label="Permalink to &quot;Посилання&quot;">​</a></h2><ol><li><a href="https://www.surveymonkey.com/product/" target="_blank" rel="noreferrer">SurveyMonkey</a></li><li><a href="https://www.survio.com/en/features" target="_blank" rel="noreferrer">Survio</a></li><li><a href="https://surveyjs.io/features" target="_blank" rel="noreferrer">SurveyJS</a></li><li><a href="https://workspace.google.com/products/forms/" target="_blank" rel="noreferrer">GoogleForms</a></li><li><a href="https://uk.wikipedia.org/wiki/%D0%9C%D0%B5%D1%82%D0%BE%D0%B4%D0%B8_%D0%B5%D0%BA%D1%81%D0%BF%D0%B5%D1%80%D1%82%D0%BD%D0%BE%D0%B3%D0%BE_%D0%BE%D1%86%D1%96%D0%BD%D1%8E%D0%B2%D0%B0%D0%BD%D0%BD%D1%8F" target="_blank" rel="noreferrer">Методи експертного оцінювання</a></li><li><a href="https://uk.wikipedia.org/wiki/%D0%9C%D0%B5%D1%82%D0%BE%D0%B4_%D0%94%D0%B5%D0%BB%D1%8C%D1%84%D1%96" target="_blank" rel="noreferrer">Метод Дельфі</a></li><li><a href="https://uk.wikipedia.org/wiki/%D0%9C%D0%BE%D0%B7%D0%BA%D0%BE%D0%B2%D0%B8%D0%B9_%D1%88%D1%82%D1%83%D1%80%D0%BC" target="_blank" rel="noreferrer">Мозковий штурм</a></li><li><a href="https://en.wikipedia.org/wiki/Scenario_analysis" target="_blank" rel="noreferrer">Сценарний метод прогнозування</a></li><li><a href="https://en.wikipedia.org/wiki/Brainwriting" target="_blank" rel="noreferrer">Метод 635</a></li><li><a href="https://en.wikipedia.org/wiki/Affinity_diagram" target="_blank" rel="noreferrer">Діаграма спорідненості</a></li><li><a href="https://www.sciencedirect.com/topics/computer-science/expert-judgment" target="_blank" rel="noreferrer">Експертні оцінки в ухваленні рішень</a></li><li><a href="https://uk.wikipedia.org/wiki/%D0%95%D0%BA%D1%81%D0%BF%D0%B5%D1%80%D1%82" target="_blank" rel="noreferrer">Експерт</a></li><li><a href="https://uk.wikipedia.org/wiki/%D0%86%D1%82%D0%B5%D1%80%D0%B0%D1%86%D1%96%D1%8F" target="_blank" rel="noreferrer">Ітера́ція</a></li><li><a href="https://goroh.pp.ua/%D0%A2%D0%BB%D1%83%D0%BC%D0%B0%D1%87%D0%B5%D0%BD%D0%BD%D1%8F/%D0%BA%D0%BE%D0%BD%D1%82%D1%80%D0%B0%D1%80%D0%B3%D1%83%D0%BC%D0%B5%D0%BD%D1%82" target="_blank" rel="noreferrer">Контраргумент</a></li><li><a href="https://uk.wikipedia.org/wiki/%D0%90%D0%BB%D0%B3%D0%BE%D1%80%D0%B8%D1%82%D0%BC" target="_blank" rel="noreferrer">Алгоритм</a></li><li><a href="https://oca.com.ua/index.php?t=101" target="_blank" rel="noreferrer">&quot;OCA&quot;</a></li><li><a href="https://uk.wikipedia.org/wiki/Google_%D0%A2%D0%B0%D0%B1%D0%BB%D0%B8%D1%86%D1%96" target="_blank" rel="noreferrer">Google Sheets</a></li><li><a href="https://gigacloud.ua/articles/shho-take-hmarni-servisy-ta-yak-vony-dopomagayut-biznesu/" target="_blank" rel="noreferrer">Хмарні сервіси</a></li><li><a href="https://www.facebook.com/story.php?story_fbid=725172066079259&amp;id=100057594153145&amp;_rdr" target="_blank" rel="noreferrer">Розширена аналітика</a></li><li><a href="https://uk.wikipedia.org/wiki/%D0%86%D0%BD%D1%82%D0%B5%D1%80%D1%84%D0%B5%D0%B9%D1%81" target="_blank" rel="noreferrer">Інтерфейс</a></li><li><a href="https://hostiq.ua/blog/ukr/what-is-api/" target="_blank" rel="noreferrer">API</a></li><li><a href="https://uk.wikipedia.org/wiki/%D0%9A%D0%B0%D1%81%D1%82%D0%BE%D0%BC%D1%96%D0%B7%D0%B0%D1%86%D1%96%D1%8F" target="_blank" rel="noreferrer">Кастомізація</a></li></ol>',77)]))}const p=t(l,[["render",o]]);export{u as __pageData,p as default};
